import { useEffect, useState } from 'react';
import {
  Routes,
  Route,
  createSearchParams,
  useSearchParams,
  useNavigate,
} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import 'reactjs-popup/dist/index.css';
import { fetchMovies } from './data/moviesSlice';
import {
  ENDPOINT_SEARCH,
  ENDPOINT_DISCOVER,
  ENDPOINT,
  API_KEY,
} from './constants';
import Header from './components/Header';
import Movies from './components/Movies';
import Starred from './components/Starred';
import WatchLater from './components/WatchLater';

import ModalVideo from './components/ModalVideo';
import './app.scss';

const App = () => {
  const state = useSelector((state) => state);
  const { movies } = state;
  const dispatch = useDispatch();
  const [searchParams, setSearchParams] = useSearchParams();
  const searchQuery = searchParams.get('search');
  const [videoKey, setVideoKey] = useState();
  const [isOpen, setOpen] = useState(false);
  const navigate = useNavigate();
  const [page, setPage] = useState(1);
  let loading = false;

  const closeModal = () => setOpen(false);

  /**
   * This function gets search results from an API
   * It takes a string argument `query` and uses it
   * to search the API.
   * The response is an array of objects, with the
   * following keys: title, url, and abstract
   * */
  const getSearchResults = (query) => {
    if (query !== '') {
      dispatch(fetchMovies(`${ENDPOINT_SEARCH}&query=` + query));
      setSearchParams(createSearchParams({ search: query }));
    } else {
      dispatch(fetchMovies(ENDPOINT_DISCOVER));
      setSearchParams();
    }
  };

  const searchMovies = (query) => {
    navigate('/');
    getSearchResults(query);
  };

  // This function gets all movies from the database.
  // It uses the database object, which is a global variable.
  // This function is called by the server when the user navigates to /movies.
  // It returns an array of movie objects.
  const getMovies = () => {
    if (searchQuery) {
      dispatch(fetchMovies(`${ENDPOINT_SEARCH}&query=` + searchQuery));
    } else {
      if (!loading) {
        loading = true;
        dispatch(fetchMovies(ENDPOINT_DISCOVER));
        console.log(loading);
        setPage(page + 1);
        setTimeout(() => {
          loading = false;
        }, 500);
      }
      // if (loaderEl) {
      //   hideLoader();
      // }
    }
  };

  /**
   * This function fetches a list of movies, and returns a list of
   * movie objects. If there are more movies to fetch, the function
   * returns a `next` function that can be used to fetch the next page
   * of movies.
   * @param page The current page to fetch, starting from 1.
   * @returns The list of movies, and a `next` function to fetch the next page.
   */
  const getMoreMovies = (page: number) => {
    loading = true;
    dispatch(fetchMovies(`${ENDPOINT_DISCOVER}'&page=` + page));
    console.log(loading);

    setTimeout(() => {
      loading = false;
      console.log(loading);
    }, 500);
  };

  const viewTrailer = (movie) => {
    getMovie(movie.id);
    setOpen(true);
  };

  // /**
  //  * Hides the loader.
  //  */
  // const hideLoader = () => {
  //   loaderEl.classList.remove('show');
  // };

  // /**
  //  * This function will add the class 'show' to the loaderEl element
  //  */
  // const showLoader = () => {
  //   loaderEl.classList.add('show');
  // };

  /**
   * This function is used to get a movie by ID.
   * It takes a movie ID as a parameter.
   * It returns a promise that resolves to the movie object.
   */
  const getMovie = async (id) => {
    const URL = `${ENDPOINT}/movie/${id}?api_key=${API_KEY}&append_to_response=videos`;

    setVideoKey(null);
    const videoData = await fetch(URL).then((response) => response.json());

    if (videoData.videos && videoData.videos.results.length) {
      const trailer = videoData.videos.results.find(
        (vid) => vid.type === 'Trailer'
      );
      setVideoKey(trailer ? trailer.key : videoData.videos.results[0].key);
    }
  };

  useEffect(() => {
    const onScroll = () => {
      const scrollTop = document.documentElement.scrollTop;
      const scrollHeight = document.documentElement.scrollHeight;
      const clientHeight = document.documentElement.clientHeight;

      if (scrollTop + clientHeight + 1 >= scrollHeight) {
        setPage(page + 1);

        console.log(page);
        if (page > 1) {
          getMoreMovies(page);
        }
      }
    };

    setTimeout(() => {
      window.addEventListener('scroll', onScroll);
    }, 400);
    return () => window.removeEventListener('scroll', onScroll);
  }, [page]);

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <div className="App">
      <Header
        searchMovies={searchMovies}
        searchParams={searchParams}
        setSearchParams={setSearchParams}
      />

      <div className="container">
        {isOpen && <ModalVideo videoKey={videoKey} closeModal={closeModal} />}

        <Routes>
          <Route
            path="/"
            element={
              <div className="movies-container">
                <Movies movies={movies} viewTrailer={viewTrailer} />
                {loading && <span id="loader" className="loader"></span>}
                {/* <span id="loader" className="loader"></span> */}
              </div>
            }
          />
          <Route
            path="/starred"
            element={<Starred viewTrailer={viewTrailer} />}
          />
          <Route
            path="/watch-later"
            element={<WatchLater viewTrailer={viewTrailer} />}
          />
          <Route
            path="*"
            element={<h1 className="not-found">Page Not Found</h1>}
          />
        </Routes>
      </div>
    </div>
  );
};

export default App;
