import { useEffect, useState } from 'react';
import {
  Routes,
  Route,
  createSearchParams,
  useSearchParams,
  useNavigate,
} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import 'reactjs-popup/dist/index.css';
import { fetchMovies } from './data/moviesSlice';
import {
  ENDPOINT_SEARCH,
  ENDPOINT_DISCOVER,
  ENDPOINT,
  API_KEY,
} from './constants';
import Header from './components/Header';
import Movies from './components/Movies';
import Starred from './components/Starred';
import WatchLater from './components/WatchLater';
import YouTubePlayer from './components/YoutubePlayer';
import './app.scss';

const App = () => {
  const state = useSelector((state) => state);
  const { movies } = state;
  const dispatch = useDispatch();
  const [searchParams, setSearchParams] = useSearchParams();
  const searchQuery = searchParams.get('search');
  const [videoKey, setVideoKey] = useState();
  const [isOpen, setOpen] = useState(false);
  const navigate = useNavigate();
  let currentPage = 1;
  const loaderEl = document.querySelector('.loader');

  window.addEventListener(
    'scroll',
    () => {
      console.log('state.fetchStatus', state.fetchStatus);
      const { scrollTop, scrollHeight, clientHeight } =
        document.documentElement;

      if (scrollTop + clientHeight >= scrollHeight - 5) {
        currentPage++;
        showLoader();
        // 0.5 second later
        setTimeout(async () => {
          try {
            getMovies(currentPage);
          } catch (error) {
            console.log(error.message);
          } finally {
            hideLoader();
          }
        }, 500);
      }
    },
    {
      passive: true,
    }
  );

  const closeModal = () => setOpen(false);

  const closeCard = () => {};

  /**
   * This function gets search results from an API
   * It takes a string argument `query` and uses it
   * to search the API.
   * The response is an array of objects, with the
   * following keys: title, url, and abstract
   * */
  const getSearchResults = (query) => {
    if (query !== '') {
      dispatch(fetchMovies(`${ENDPOINT_SEARCH}&query=` + query));
      setSearchParams(createSearchParams({ search: query }));
    } else {
      dispatch(fetchMovies(ENDPOINT_DISCOVER));
      setSearchParams();
    }
  };

  const searchMovies = (query) => {
    navigate('/');
    getSearchResults(query);
  };

  /**
   * This function fetches a page of movies from the API, and
   * returns the results in a Promise.
   //
   * Function parameters:
   * - currentPage: The page number to fetch.
   //
   * Returns:
   * - Promise of an array of movies.
   */
  const getMovies = (currentPage: number) => {
    if (searchQuery) {
      dispatch(fetchMovies(`${ENDPOINT_SEARCH}&query=` + searchQuery));
    } else if (currentPage) {
      dispatch(fetchMovies(`${ENDPOINT_DISCOVER}'&page=` + currentPage));
    } else {
      dispatch(fetchMovies(ENDPOINT_DISCOVER));
    }
  };

  const viewTrailer = (movie) => {
    getMovie(movie.id);
    if (!videoKey) setOpen(true);
    setOpen(true);
  };

  /**
   * Hides the loader.
   */
  const hideLoader = () => {
    loaderEl.classList.remove('show');
  };

  /**
   * This function will add the class 'show' to the loaderEl element
   */
  const showLoader = () => {
    loaderEl.classList.add('show');
  };

  /**
   * This function is used to get a movie by ID.
   * It takes a movie ID as a parameter.
   * It returns a promise that resolves to the movie object.
   */
  const getMovie = async (id) => {
    const URL = `${ENDPOINT}/movie/${id}?api_key=${API_KEY}&append_to_response=videos`;

    setVideoKey(null);
    const videoData = await fetch(URL).then((response) => response.json());

    if (videoData.videos && videoData.videos.results.length) {
      const trailer = videoData.videos.results.find(
        (vid) => vid.type === 'Trailer'
      );
      setVideoKey(trailer ? trailer.key : videoData.videos.results[0].key);
    }
  };

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <div className="App">
      <Header
        searchMovies={searchMovies}
        searchParams={searchParams}
        setSearchParams={setSearchParams}
      />

      <div className="container">
        {videoKey ? (
          <YouTubePlayer videoKey={videoKey} />
        ) : (
          <div style={{ padding: '30px' }}>
            <h6>no trailer available. Try another movie</h6>
          </div>
        )}

        <Routes>
          <Route
            path="/"
            element={
              <div className="movies-container">
                <Movies
                  movies={movies}
                  viewTrailer={viewTrailer}
                  closeCard={closeCard}
                />
                <span className="loader"></span>
              </div>
            }
          />
          <Route
            path="/starred"
            element={<Starred viewTrailer={viewTrailer} />}
          />
          <Route
            path="/watch-later"
            element={<WatchLater viewTrailer={viewTrailer} />}
          />
          <Route
            path="*"
            element={<h1 className="not-found">Page Not Found</h1>}
          />
        </Routes>
      </div>
    </div>
  );
};

export default App;
